import React, { Component } from "react";
import "./App.css";
import InputCurrency from "./components/inputCurrency";
import Currency from "./components/currency";
import axios from "axios";

class App extends Component {
  //set default currency
  state = {
    listCurrency: [
      { id: "IDR", text: "Indonesian Rupiah" },
      { id: "EUR", text: "Euro" },
      { id: "GBP", text: "British Pound" },
      { id: "SGD", text: "Singapore Dollars" }
    ]
  };

  async componentDidMount() {
    const {
      data: { rates }
    } = await axios.get("https://api.exchangeratesapi.io/latest?base=USD");
    console.log(rates);
  }

  //custom styles
  cardStyles = {
    maxWidth: "18rem"
  };

  render() {
    return (
      <div style={this.cardStyles} className="card text-white bg-primary mb-3">
        <div className="card-header">
          <InputCurrency />
        </div>
        <div className="card-body">
          <ul>
            {this.state.listCurrency.map(row => (
              <li key={row.id}>
                <Currency key={row.id} dataCurrency={row} />
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
