import React, { Component } from "react";
import axios from "axios";
class Currency extends Component {
  state = {
    id: "",
    text: "",
    rate: 0
  };

  async componentDidMount() {
    const promise = axios.get(
      "https://api.exchangeratesapi.io/latest?base=USD"
    ); //hold result of an asynchronous operation
    const response = await promise;
    console.log(response);
  }

  constructor(props) {
    super(props);
    this.state.id = this.props.dataCurrency.id;
    this.state.text = this.props.dataCurrency.text;
  }
  render() {
    return (
      <React.Fragment>
        <div className="col-6">{this.state.id}</div>
        <div className="col-6">{this.state.rate}</div>
        <div className="col-12">
          <i>
            {this.state.id} - {this.state.text}
          </i>
        </div>
        <div className="col-12">
          <i>
            1 USD = {this.state.id} - {this.state.rate}
          </i>
        </div>
      </React.Fragment>
    );
  }
}

export default Currency;
