import React, { Component } from "react";
import "./App.css";
import InputCurrency from "./components/inputCurrency";
import Currency from "./components/currency";
import axios from "axios";

class App extends Component {
  //set default currency
  state = {
    listCurrency: [
      { id: "CAD", text: "Canada Dollar", default: false, rate: 0 },
      { id: "IDR", text: "Indonesian Rupiah", default: true, rate: 0 },
      { id: "CHF", text: "Switzerland Franch", default: false, rate: 0 },
      { id: "EUR", text: "Euro", default: true, rate: 0 },
      { id: "GBP", text: "British Pound", default: true, rate: 0 },
      { id: "SGD", text: "Singapore Dollars", default: true, rate: 0 },
      { id: "INR", text: "India Rupee", default: false, rate: 0 },
      { id: "MYR", text: "Malaysian Ringgit", default: false, rate: 0 },
      { id: "JPY", text: "Japanese Yen", default: false, rate: 0 },
      { id: "KRW", text: "Korea Won", default: false, rate: 0 }
    ]
  };

  async componentDidMount() {
    const {
      data: { rates }
    } = await axios.get("https://api.exchangeratesapi.io/latest?base=USD");
    var result = Object.keys(rates).map(function(key) {
      return [Number(key), obj[key]];
    });
    console.log(result);
  }

  //custom styles
  cardStyles = {
    maxWidth: "18rem"
  };

  render() {
    const listDefaultCurrency = this.state.listCurrency.filter(
      c => c.default === true
    );
    return (
      <div style={this.cardStyles} className="card text-white bg-primary mb-3">
        <div className="card-header">
          <InputCurrency />
        </div>
        <div className="card-body">
          <ul>
            {listDefaultCurrency.map(row => (
              <li key={row.id}>
                <Currency key={row.id} dataCurrency={row} />
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
