import React, { Component } from "react";
class NewCurrency extends Component {
  render() {
    const { listCurrency, onSubmit } = this.props;
    const optionValue = listCurrency.map(row => (
      <option key={row.id}>{row.text}</option>
    ));
    return (
      <div className="input-group">
        <select className="custom-select">{optionValue}</select>
        <div className="input-group-append">
          <button
            className="btn btn-outline-secondary"
            onClick={onSubmit}
            type="button"
          >
            Submit
          </button>
        </div>
      </div>
    );
  }
}

export default NewCurrency;
